import React, {Component} from 'react';
// import logo from '../logo.svg';
import './App.scss';
import {BrowserRouter as Router, Redirect} from 'react-router-dom';
import Login from '../components/home/login';
import Register from '../components/home/register';
import Nav from './main';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// import Todo from '../components/todo-list/todo';

class App extends Component {

  state = {
    showLogin: true,
    loggedIn: false
  }

  logIn = () => {
    this.setState(prevState => ({loggedIn: !prevState.loggedIn}));
  }

  changeState =(event) => {
      this.setState(prevState => ({showLogin: !prevState.showLogin}));
  }

  render () {
    const showLogin = this.state.showLogin;
    const loggedIn = this.state.loggedIn;
    console.log(this.state);
    return(
      <Router>
        <Container>
          <Row>
            <Col>
              <header className="text-center">
                {/* <img src={logo} className="App-logo" alt="logo" /> */}
                <h1>LOGO</h1>
              </header>
            </Col>
          </Row>
          <Row className="justify-content-md-center">
              {(() => {
                  if(loggedIn) {
                    console.log("if");
                    return <Redirect to="/todolist" logOut={this.logIn} />;
                      // return <Redirect to="/todolist" />
                  } else {
                    return <Col xs={12} md={4}>
                      {showLogin && <Login userLoggedIn={this.logIn} changeForm={this.changeState}></Login>}
                      {!showLogin && <Register changeForm={this.changeState}></Register>}
                    </Col>
                  }
              })()}
            </Row>
        </Container> 
        <Nav></Nav>
      </Router>
    )
  };
}

export default App;
