import React, {Component} from 'react';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Modal from 'react-bootstrap/Modal'
import './todo.scss';

class ToDo extends Component {
    addNewTask = () => {
        const url = 'https://engine-staging.viame.ae/assessment/user/task';
        const data = {users: { email: this.state.email, password: this.state.password }}
        console.log("Data", data);
        fetch(url, { 
            method: 'POST',
            body: JSON.stringify(data),
            headers:{ 'Content-Type': 'application/json' } 
        }).then(res => {
            res.json();
            // if() {
                // this.setState({loggedIn: true});
                this.props.userLoggedIn();
            // }
        }).catch(error => console.error('Error:', error)
        ).then(response => console.log('Success:', response));
    }
    render() {
        return (
            <Container className="todoBox">
                {/* <Button variant="primary" type="submit">
                            Logout
                </Button> */}
                <Row>
                    <Col className="text-right">
                        <Button id="newTask" variant="primary" onClick={this.addNewTask} type="submit">
                            Add New Task
                        </Button>  
                    </Col>
                </Row>
                <Row>
                    <Table responsive>
                        <thead>
                            <tr>
                                <th>Task</th>
                                <th>Description</th>
                                <th>Status</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>TQuo aut aut cillum sit architecto adt nihil et ducimus </td>
                                <td>Autemique et quis hic sed sunt ut earum ex sapiente</td>
                                <td>Working</td>
                                <td>
                                    <Dropdown>
                                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                                            Actions
                                        </Dropdown.Toggle>
                                        <Dropdown.Menu>
                                            <Dropdown.Item href="#/action-1">Done</Dropdown.Item>
                                            <Dropdown.Item href="#/action-2">Created</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Working</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Cancelled</Dropdown.Item>
                                        </Dropdown.Menu>
                                    </Dropdown>
                                </td>
                            </tr>
                            <tr>
                                <td>Ttecto adt nihil etmus </td>
                                <td>Autem neque sed omnis exerx sapiente</td>
                                <td>Working</td>
                                <td>
                                    <Dropdown>
                                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                                            Actions
                                        </Dropdown.Toggle>
                                        <Dropdown.Menu>
                                            <Dropdown.Item href="#/action-1">Done</Dropdown.Item>
                                            <Dropdown.Item href="#/action-2">Created</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Working</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Cancelled</Dropdown.Item>
                                        </Dropdown.Menu>
                                    </Dropdown>
                                </td>
                            </tr>
                        </tbody>
                    </Table>
                </Row>
            </Container>
        )
    }
}

export default ToDo;
