import React, {Component} from 'react';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import {Redirect} from 'react-router-dom';
import './home.scss';
// import Nav from '../../containers/main';

class login extends Component {
    state = {
        email: "",
        password: "",
        loggedIn: false
    }

    handleChange = event =>{
        this.setState({ [event.target.name]:event.target.value })
    }

    submitForm = (event) => {
        event.preventDefault();
        // if() {
            this.setState({loggedIn: true});
        // }
        console.log('Userpwd:', this.state.password);
        console.log('User Email' + this.state.email);
        const { email, password} = this.state;
        // console.log("Data", data);
        // fetch('https://engine-staging.viame.ae/assessment/login', {
        //   method: 'POST',
        //   body: data,
        // });
    }
    render() {
        if(this.state.loggedIn) {
            return <Redirect to="/todolist" />
        }
        return (
            <Form onSubmit={this.submitForm} className="text-center loginForm">
                <Form.Group controlId="EmailInput">
                    <InputGroup>
                        <InputGroup.Prepend>
                            <InputGroup.Text><i className="fa fa-envelope"></i></InputGroup.Text>
                        </InputGroup.Prepend>
                        <Form.Control type="email" name="email" placeholder="Enter your Email ID" aria-describedby="inputGroupPrepend" required />
                        <Form.Control.Feedback type="invalid">Please enter your email address.</Form.Control.Feedback>
                    </InputGroup>
                </Form.Group>
                <Form.Group controlId="PwdInput">
                    <InputGroup>
                        <InputGroup.Prepend>
                            <InputGroup.Text><i className="fa fa-lock"></i></InputGroup.Text>
                        </InputGroup.Prepend>
                        <Form.Control type="password" name="password" placeholder="Enter your Password" aria-describedby="inputGroupPrepend" required />
                        <Form.Control.Feedback type="invalid">Please enter your password.</Form.Control.Feedback>
                    </InputGroup>
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit 
                </Button>
                <Button className="redirectButton" onClick={this.props.changeForm}>Don't have an account? Register</Button>
            </Form>
        )
    }
}

export default login;
