import React, {Component} from 'react';
import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Modal from 'react-bootstrap/Modal';
import './todo.scss';

class ToDo extends Component {
    state = {
        title: "",
        description: "",
        status: "",
        show : false
    }

    handleClose = () => this.setState({show: false});
    handleShow = () => this.setState({show: true});

    handleModalChange = event => {
        this.setState({ [event.target.name]:event.target.value })
    }

    addNewTask = () => {
        console.log("Add Task");
        const url = 'https://engine-staging.viame.ae/assessment/user/task';
        const data = {todolist: { title: this.state.title, description: this.state.description, status:1 }}
        console.log("Data", data);
        fetch(url, { 
            method: 'POST',
            body: JSON.stringify(data),
            headers:{ 'Content-Type': 'application/json', 'x-access-token': 'ca7fe1db6188a235dabc9c1457d82e636b11a543' } 
        }).then(res => {
            res.json();
            this.handleClose();
            // refreshTasks();
        }).catch(error => console.error('Error:', error)
        ).then(response => console.log('Success:', response));
    }
    logOut = () => {
        localStorage.removeItem("token");
    }

    render() {
        const show = this.state.show;
        return (
            <Container className="todoList">
                <Row>
                    <Col className="text-right">
                        <Button id="logOut" variant="secondary" type="button" onClick={this.logOut}>Logout</Button>
                        <Button id="newTask" variant="primary" onClick={this.handleShow} type="button"> + New Task</Button>  
                    </Col>
                </Row>
                <Row className="todoBox">
                    <Table responsive>
                        <thead>
                            <tr>
                                <th>Task</th>
                                <th>Description</th>
                                <th>Status</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>Task title </td>
                                <td>Task Description</td>
                                <td>Working</td>
                                <td>
                                    <Dropdown>
                                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                                            Actions
                                        </Dropdown.Toggle>
                                        <Dropdown.Menu>
                                            <Dropdown.Item href="#/action-1">Done</Dropdown.Item>
                                            <Dropdown.Item href="#/action-2">Created</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Working</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Cancelled</Dropdown.Item>
                                        </Dropdown.Menu>
                                    </Dropdown>
                                </td>
                            </tr>
                            <tr>
                                <td>Task Title </td>
                                <td>Task Description</td>
                                <td>Working</td>
                                <td>
                                    <Dropdown>
                                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                                            Actions
                                        </Dropdown.Toggle>
                                        <Dropdown.Menu>
                                            <Dropdown.Item href="#/action-1">Done</Dropdown.Item>
                                            <Dropdown.Item href="#/action-2">Created</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Working</Dropdown.Item>
                                            <Dropdown.Item href="#/action-3">Cancelled</Dropdown.Item>
                                        </Dropdown.Menu>
                                    </Dropdown>
                                </td>
                            </tr>
                        </tbody>
                    </Table>
                </Row>
                <Modal show={show} onHide={this.handleClose} className="newModal">
                    <Modal.Header closeButton>
                        <Modal.Title>Add New Task</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form className="text-center newTaskForm">
                            <Form.Group controlId="title">
                                <InputGroup>
                                    <Form.Control type="text" name="title" placeholder="Enter task title" onChange={this.handleModalChange} required />
                                    <Form.Control.Feedback type="invalid">Please enter title for the task</Form.Control.Feedback>
                                </InputGroup>
                            </Form.Group>
                            <Form.Group controlId="description">
                                <InputGroup>
                                    <Form.Control as="textarea" type="text" name="description" placeholder="Enter task description" onChange={this.handleModalChange} required />
                                    <Form.Control.Feedback type="invalid">Please enter description for the task</Form.Control.Feedback>
                                </InputGroup>
                            </Form.Group>
                        </Form>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>Close</Button>
                        <Button variant="primary" onClick={this.addNewTask}>Add Task</Button>
                    </Modal.Footer>
                </Modal>
            </Container>
        )
    }
}

export default ToDo;
