import React, {Component} from 'react';
// import logo from '../logo.svg';
import './App.scss';
import {BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';
import Login from '../components/home/login';
import Register from '../components/home/register';
// import Nav from './main';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Todo from '../components/todo-list/todo';

class App extends Component {

  state = {
    showLogin: true,
    loggedIn: false
  }

  // componentDidMount = () => {
  //   const tok = localStorage.getItem("token");
  //   if(tok) {
  //     this.logIn();
  //   }
  // }

  logIn = () => {
    // const tok = localStorage.getItem("token");
    // if(tok) {
    //   this.setState(() => ({loggedIn: true}));
    // } else {
    //   this.setState(() => ({loggedIn: false}));
      this.setState(prevState => ({loggedIn: !prevState.loggedIn}));
    // }
  }

  changeState =(event) => {
      this.setState(prevState => ({showLogin: !prevState.showLogin}));
  }

  render () {
    const showLogin = this.state.showLogin;
    const loggedIn = this.state.loggedIn;
    let styles = {};
    if (loggedIn) {styles = {"background": "linear-gradient(to right, #78ffd6, #007991);"}}
    console.log(this.state);
    return(
      <Router>
        <Container fluid={true}>
          <Row style={styles}>
            <Col>
              <header className="text-center">
                {/* <img src={logo} className="App-logo" alt="logo" /> */}
                <h1>LOGO</h1>
              </header>
            </Col>
          </Row>
          {/* <Row>
              {(() => {
                  if(loggedIn) {
                    return <Redirect to="/todolist" />;
                  } else {
                    return <Redirect to="/" />;
                  }
              })()}
            </Row> */}
        </Container> 
        <Switch>
            <Route path="/" exact render={()=> (
                <Row className="justify-content-md-center"><Col xs={12} md={4}>
                {showLogin && <Login userLoggedIn={this.logIn} changeForm={this.changeState}></Login>}
                {!showLogin && <Register changeForm={this.changeState}></Register>}</Col></Row>
            )} />
            <Route path="/todolist" exact render={(props) => {
              if (loggedIn) {
                return (<div className="todo-background">
                { <Todo {...props}  userLoggedOut={this.logIn} />}</div>);
              } else {
                return (<Redirect to="/" />);
              }
            }} />
        </Switch>
      </Router>
    )
  };
}

export default App;
